    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        String idCliente = idClienteTxt.getText();
        String tipoTrans = tipoT.getSelectedItem().toString();
        bank.addToCaja(idCliente, tipoTrans);


    }//GEN-LAST:event_jButton5ActionPerformed

    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
        Caja S = bank.getPtr();
        bank.Mostrar(S);

    }//GEN-LAST:event_jButton6ActionPerformed

    private void Btn_deleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Btn_deleteActionPerformed
        int Id = Integer.parseInt(JOptionPane.showInputDialog("Ingrese ID de la Caja"));
        Caja R = bank.getPtr();
        bank.EliminarCaja(R, Id);

    }//GEN-LAST:event_Btn_deleteActionPerformed

